void draw_flappy(int x, int y) {
  if (flappy_wing >= 0 && flappy_wing < 20) {
    monitor.drawImageF(x, y, BIRD_W, BIRD_H, flying_bird);
  }
  else if (flappy_wing >= 20 && flappy_wing < 40) {
    monitor.drawImageF(x, y, BIRD_W, BIRD_H, floating_bird);
  }
  else if (flappy_wing >= 40 && flappy_wing < 60) {
    monitor.drawImageF(x, y, BIRD_W, BIRD_H, gliding_bird);
  }
  else if (flappy_wing >= 60 && flappy_wing < 80) {
    monitor.drawImageF(x, y, BIRD_W, BIRD_H, floating_bird);
  }
}

void clear_flappy(int x, int y) {
  monitor.fillRect(x, y, BIRD_W, BIRD_H, CYAN); 
}
